import React, { useRef } from 'react'
import axios from 'axios';
import { Button, InputGroup, FormControl } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import fetchAPIData from '../../ api/api';


export default function SearchField({ setInputData, setResultData }) {

    const inputField = useRef(null)

    const handleSubmit = () => {
        const input = inputField.current
        setInputData(input.pokemonQuery.value)
        fetchAPIData(input.pokemonQuery.value)
            .then(data => {
                console.log(data)
                setResultData(data)
        })
    }

    /* const fetchAPIData = async (searchQuery) => {
        let uri = `http://localhost:3001/pokemon/${searchQuery}`
        return await axios.get(uri).then(response => response.data).catch((err) => console.error(err)) 
    } */

    return (
        <InputGroup className="mb-3">
            <form ref={inputField}>
                <FormControl
                placeholder="Search for pokemon"
                aria-label="search-for-pokemon"
                aria-describedby="search"
                name='pokemonQuery'
                />
            </form>
            <Button 
                variant="outline-secondary" 
                id="submit"
                onClick={() => {
                    handleSubmit()
                }}
            >
                Search
            </Button>
        </InputGroup>
    )
}
