import axios from 'axios'

export default async function fetchAPIData(searchQuery) {
    let uri = `http://localhost:3001/pokemon/${searchQuery}`
    let res = await axios.get(uri).then(response => response.data).catch((err) => console.error(err)) 
    return res.json()
}
