import React, { useState, useRef } from 'react';
import './App.css';
import axios from 'axios';
import { Button, InputGroup, FormControl, Row } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import PokeCard from './components/PokeCard/PokeCard';

function App() {
  const [ searchQuery, setSearchQuery] = useState('') 
  const [ resultData, setResultData] = useState()

  const inputField = useRef(null)

  let api = `http://localhost:3001/pokemon/${searchQuery}`

  const handleSubmit = () => {
    const input = inputField.current
    console.log(input)
    //fetchAPIData()
  }

  const fetchAPIData = () => {
    return axios.get(api)
    .then(({data}) => {
      console.log(data)
      setResultData(data)
    })
    .catch((err) => console.error(err))
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
            <InputGroup className="mb-3">
              <form ref={inputField}>
                  <FormControl
                    placeholder="Search for pokemon"
                    aria-label="Search for pokemon"
                    aria-describedby="search"
                    name='pokemonQuery'
                    //onChange={(e) => { setSearchQuery(e.target.value) }}
                  />
              </form>
              <Button 
                variant="outline-secondary" 
                id="submit"
                onClick={() => {
                  handleSubmit()
                }}
              >
                Search
              </Button>
            </InputGroup>
        </div>
        <div>
          {resultData &&<PokeCard info={resultData} />}
        </div>
      </header>
    </div>
  );


}

export default App;
