import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import PokeCard from './components/PokeCard/PokeCard';
import SearchField from './components/search/SearchField';

function App() {
  const [ resultData, setResultData] = useState()
  const [ inputData, setInputtData] = useState('')
  //const inputField = useRef(null)

  /* const handleSubmit = () => {
    const input = inputField.current
    setInputtData(input.pokemonQuery.value)
    fetchAPIData(input.pokemonQuery.value)
  } */

  const fetchAPIData = (searchQuery) => {
    return axios.get(`http://localhost:3001/pokemon/${searchQuery}`)
    .then(({data}) => {
      setResultData(data)
    })
    .catch((err) => console.error(err))
  }

  useEffect(() => {
    fetchAPIData()
  }, [inputData])


  return (
    <div className="App">
      <header className="App-header">
        <div>
            <SearchField 
              setInputtData={setInputtData}
            />
        </div>
        <div>
          {resultData &&<PokeCard info={resultData} inputData={inputData} />}
        </div>
      </header>
    </div>
  );


}

export default App;
