import React, { useRef,useState } from 'react'
import axios from 'axios';
import { Button, InputGroup, FormControl } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'

export default function SearchField({ setInputtData, setResultData }) {

    const inputField = useRef(null)

    const handleSubmit = () => {
        const input = inputField.current
        setInputtData(input.pokemonQuery.value)
        fetchAPIData(input.pokemonQuery.value)
    }

    const fetchAPIData = (searchQuery) => {
        return axios.get(`http://localhost:3001/pokemon/${searchQuery}`)
        .then(({data}) => {
          setResultData(data)
        })
        .catch((err) => console.error(err))
    }

    return (
        <InputGroup className="mb-3">
            <form ref={inputField}>
                <FormControl
                placeholder="Search for pokemon"
                aria-label="Search for pokemon"
                aria-describedby="search"
                name='pokemonQuery'
                />
            </form>
            <Button 
                variant="outline-secondary" 
                id="submit"
                onClick={() => {
                    handleSubmit()
                }}
            >
                Search
            </Button>
        </InputGroup>
    )
}
