import React, { useState, useRef } from 'react';
import './App.css';
import axios from 'axios';
import { Button, InputGroup, FormControl, Row } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import PokeCard from './components/PokeCard/PokeCard';

function App() {
  const [ resultData, setResultData] = useState()

  const inputField = useRef(null)

  const handleSubmit = () => {
    const input = inputField.current
    fetchAPIData(input.pokemonQuery.value)
  }

  const fetchAPIData = (searchQuery) => {
    return axios.get(`http://localhost:3001/pokemon/${searchQuery}`)
    .then(({data}) => {
      console.log(data)
      setResultData(data)
    })
    .catch((err) => console.error(err))
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
            <InputGroup className="mb-3">
              <form ref={inputField}>
                  <FormControl
                    placeholder="Search for pokemon"
                    aria-label="Search for pokemon"
                    aria-describedby="search"
                    name='pokemonQuery'
                  />
              </form>
              <Button 
                variant="outline-secondary" 
                id="submit"
                onClick={() => {
                  handleSubmit()
                }}
              >
                Search
              </Button>
            </InputGroup>
        </div>
        <div>
          {resultData &&<PokeCard info={resultData} />}
        </div>
      </header>
    </div>
  );


}

export default App;
